/*
* define a few functions to work with highstock
*/

var HighstockController = {};
/*
* connect to a WebSocket server
* @param {string} id : id of the div to draw the chart on, without "#"
* @return {Object} socket object generated by socket.io
*/

HighstockController.draw = function(id) {
    // chart is auto-rendered by object declaration
    // eslint-disable-next-line
    var myChart = Highcharts.stockChart({
        "chart": {
            "type": "candlestick", // line or candlestick
            "renderTo": id
        },
        "title": {
            "text": "Stock Prices"
        },
        "subtitle": {
            "text": "Good luck!"
        },
        "series": [
            {
                "name" : "MSFT",
                "data" : [
                    {
                        "x": 1476532800000,
                        "open": 20,
                        "high": 30,
                        "low": 21,
                        "close": 28
                    },
                    {
                        "x": 1476619200000,
                        "open": 28,
                        "high": 35,
                        "low": 26,
                        "close": 31
                    }
                ]
            }
        ]
    });
};
